# asks if you want to do it again
def again():
    question = input("\nAgain? yes or no\n")
    while True:
        if question == "y" or question == "yes":
            find(element_name, symbol, atomic_number, avg_mass, neutrons, ions, type)
            break
        elif question == "n" or question == "no":
            print("\nDone")
            break
        else:
            question = input("\nPlease answer yes or no\n")
            

# finds all of the properties of specified element
def find(a, b, c, d, e, f, g):
    element_request = input("***********************************************\n\nWhat element: ")
    while True:
        if element_request == "exit": # user input exit
            print("\nDone")
            break
        elif element_request not in atomic_number.keys(): # if the specified element you entered is not in the periodic table, you will be asked to re-enter it again
            element_request = input("\nPlease re-enter the element: ")
        else:
            print("")
            if element_request in a.keys():
                print("Element name: " + str(a[element_request]))
            if element_request in b.keys():
                print("Element symbol: " + str(b[element_request]))
            if element_request in c.keys():
                print("Atomic number: " + str(c[element_request]))
            if element_request in d.keys():
                print("Average mass: " + str(d[element_request]))  
            if element_request in e.keys():
                print("Number of neutrons: " + str(e[element_request]))
            if element_request in f.keys():
                print("Number of ions: " + str(f[element_request]))
            if element_request in g.keys():
                print("Type of material: " + str(g[element_request])) 
            again()
            break
            
